#' Bayesian ANCOVA
#'
#' 
#' @param data .
#' @param dep .
#' @param factors .
#' @param random .
#' @param covs .
#' @param bfType .
#' @param order .
#' @param effects .
#' @param desc .
#' @param effectsType .
#' @param modelTerms .
#' @param postHoc .
#' @param postHocCorr .
#' @param plotHz .
#' @param plotLines .
#' @param plotSep .
#' @param plotCI .
#' @param ciWidth .
#' @param rScaleFixed .
#' @param rScaleRand .
#' @param rScaleCovs .
#' @param sampling .
#' @param nSamples .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$model_comparison} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$effects} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$posthoc} \tab \tab \tab \tab \tab an array of tables \cr
#'   \code{results$descriptivesObj$descriptivesTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$descriptivesObj$descriptivesPlot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$model_comparison$asDF}
#'
#' \code{as.data.frame(results$model_comparison)}
#'
#' @export
bancova <- function(
    data,
    dep,
    factors,
    random = NULL,
    covs,
    bfType = "BF10",
    order = "nullModelTop",
    effects = FALSE,
    desc = FALSE,
    effectsType = "allModels",
    modelTerms = NULL,
    postHoc = NULL,
    postHocCorr = TRUE,
    plotHz = NULL,
    plotLines = NULL,
    plotSep = NULL,
    plotCI = FALSE,
    ciWidth = 95,
    rScaleFixed = 0.5,
    rScaleRand = 1,
    rScaleCovs = 0.354,
    sampling = "auto",
    nSamples = 10000) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("bancova requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(factors)) factors <- jmvcore::resolveQuo(jmvcore::enquo(factors))
    if ( ! missing(random)) random <- jmvcore::resolveQuo(jmvcore::enquo(random))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if ( ! missing(postHoc)) postHoc <- jmvcore::resolveQuo(jmvcore::enquo(postHoc))
    if ( ! missing(plotHz)) plotHz <- jmvcore::resolveQuo(jmvcore::enquo(plotHz))
    if ( ! missing(plotLines)) plotLines <- jmvcore::resolveQuo(jmvcore::enquo(plotLines))
    if ( ! missing(plotSep)) plotSep <- jmvcore::resolveQuo(jmvcore::enquo(plotSep))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(factors), factors, NULL),
            `if`( ! missing(random), random, NULL),
            `if`( ! missing(covs), covs, NULL),
            `if`( ! missing(postHoc), postHoc, NULL),
            `if`( ! missing(plotHz), plotHz, NULL),
            `if`( ! missing(plotLines), plotLines, NULL),
            `if`( ! missing(plotSep), plotSep, NULL))


    options <- bancovaOptions$new(
        dep = dep,
        factors = factors,
        random = random,
        covs = covs,
        bfType = bfType,
        order = order,
        effects = effects,
        desc = desc,
        effectsType = effectsType,
        modelTerms = modelTerms,
        postHoc = postHoc,
        postHocCorr = postHocCorr,
        plotHz = plotHz,
        plotLines = plotLines,
        plotSep = plotSep,
        plotCI = plotCI,
        ciWidth = ciWidth,
        rScaleFixed = rScaleFixed,
        rScaleRand = rScaleRand,
        rScaleCovs = rScaleCovs,
        sampling = sampling,
        nSamples = nSamples)

    analysis <- bancovaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

