#' Bayesian Log-Linear Regression
#'
#' 
#' @param data .
#' @param factors .
#' @param counts .
#' @param bfType .
#' @param priorShape .
#' @param priorScale .
#' @param maxModels .
#' @param postProbCutOff .
#' @param modelTerms .
#' @param est .
#' @param ci .
#' @param ciWidth .
#' @param sub .
#' @param subNo .
#' @param subEst .
#' @param subCI .
#' @param subCIWidth .
#' @param sampling .
#' @param nSamples .
#' @return A results object containing:
#' \tabular{llllll}{
#' }
#'
#' @export
blogLinear <- function(
    data,
    factors,
    counts = NULL,
    bfType = "BF10",
    priorShape = -1,
    priorScale = 0,
    maxModels = 2,
    postProbCutOff = 0.1,
    modelTerms = NULL,
    est = FALSE,
    ci = FALSE,
    ciWidth = 95,
    sub = FALSE,
    subNo = 1,
    subEst = FALSE,
    subCI = FALSE,
    subCIWidth = 95,
    sampling = "auto",
    nSamples = 10000) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("blogLinear requires jmvcore to be installed (restart may be required)")

    if ( ! missing(factors)) factors <- jmvcore::resolveQuo(jmvcore::enquo(factors))
    if ( ! missing(counts)) counts <- jmvcore::resolveQuo(jmvcore::enquo(counts))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(factors), factors, NULL),
            `if`( ! missing(counts), counts, NULL))

    if (inherits(modelTerms, "formula")) modelTerms <- jmvcore::decomposeFormula(modelTerms)

    options <- blogLinearOptions$new(
        factors = factors,
        counts = counts,
        bfType = bfType,
        priorShape = priorShape,
        priorScale = priorScale,
        maxModels = maxModels,
        postProbCutOff = postProbCutOff,
        modelTerms = modelTerms,
        est = est,
        ci = ci,
        ciWidth = ciWidth,
        sub = sub,
        subNo = subNo,
        subEst = subEst,
        subCI = subCI,
        subCIWidth = subCIWidth,
        sampling = sampling,
        nSamples = nSamples)

    analysis <- blogLinearClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

