#' Bayesian Independent Samples T-Test
#'
#' 
#' @param data .
#' @param vars .
#' @param group .
#' @param hypothesis .
#' @param bfType .
#' @param test .
#' @param mannN .
#' @param desc .
#' @param plotPP .
#' @param plotPPAddInfo .
#' @param plotRobust .
#' @param plotRobustAddInfo .
#' @param plotSeq .
#' @param plotSeqRobust .
#' @param plotDesc .
#' @param plotDescCI .
#' @param missing .
#' @param priorType .
#' @param priorWidth .
#' @param infoShape .
#' @param infoCL .
#' @param infoCS .
#' @param infoNL .
#' @param infoNS .
#' @param infoTL .
#' @param infoTS .
#' @param infoTDf .
#' @param plotWidth .
#' @param plotHeight .
#' @param formula (optional) the formula to use, see the examples
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$ttest} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$descriptives$descriptivesTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$descriptives$descriptivesPlots} \tab \tab \tab \tab \tab an array \cr
#'   \code{results$inferentialPlots} \tab \tab \tab \tab \tab an array of groups \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$ttest$asDF}
#'
#' \code{as.data.frame(results$ttest)}
#'
#' @export
bttestIS <- function(
    data,
    vars,
    group,
    hypothesis = "different",
    bfType = "BF10",
    test = "students",
    mannN = 1000,
    desc = FALSE,
    plotPP = FALSE,
    plotPPAddInfo = TRUE,
    plotRobust = FALSE,
    plotRobustAddInfo = TRUE,
    plotSeq = FALSE,
    plotSeqRobust = FALSE,
    plotDesc = FALSE,
    plotDescCI = 95,
    missing = "perAnalysis",
    priorType = "default",
    priorWidth = 0.707,
    infoShape = "cauchy",
    infoCL = 0,
    infoCS = 0.707,
    infoNL = 0,
    infoNS = 0.707,
    infoTL = 0,
    infoTS = 0.707,
    infoTDf = 1,
    plotWidth = 320,
    plotHeight = 240,
    formula) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("bttestIS requires jmvcore to be installed (restart may be required)")

    if ( ! missing(formula)) {
        if (missing(vars))
            vars <- jmvcore::marshalFormula(
                formula=formula,
                data=`if`( ! missing(data), data, NULL),
                from="lhs",
                required=TRUE)
        if (missing(group))
            group <- jmvcore::marshalFormula(
                formula=formula,
                data=`if`( ! missing(data), data, NULL),
                from="rhs",
                subset="1")
    }

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- bttestISOptions$new(
        vars = vars,
        group = group,
        hypothesis = hypothesis,
        bfType = bfType,
        test = test,
        mannN = mannN,
        desc = desc,
        plotPP = plotPP,
        plotPPAddInfo = plotPPAddInfo,
        plotRobust = plotRobust,
        plotRobustAddInfo = plotRobustAddInfo,
        plotSeq = plotSeq,
        plotSeqRobust = plotSeqRobust,
        plotDesc = plotDesc,
        plotDescCI = plotDescCI,
        missing = missing,
        priorType = priorType,
        priorWidth = priorWidth,
        infoShape = infoShape,
        infoCL = infoCL,
        infoCS = infoCS,
        infoNL = infoNL,
        infoNS = infoNS,
        infoTL = infoTL,
        infoTS = infoTS,
        infoTDf = infoTDf,
        plotWidth = plotWidth,
        plotHeight = plotHeight)

    analysis <- bttestISClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

